exports.setYInstance=function(c){
	Y = c;
};

var hash = function (pwd, salt){
  var shasum = crypto.createHash('sha1');
  shasum.update(pwd+salt);
  return shasum.digest('hex');
};
function login(config,callback){
	var username = config.username, password = config.password,user;
	db.view('user/byEmail', { key: username }, function (err, docs) {
      if(docs && docs.length>0){
      	user = docs.pop();
      	
      	if(user.value.password===api.user.hash_password({val:password})){
      		if(Y.Lang.isFunction(callback)){ 
      			callback(null,{
	      			success:true,
	      			user:user.value
      			});
      			Y.fire("UpdateUser",{user:user.value,success:true});
      		}
      	}
      	else
      	{
      		if(Y.Lang.isFunction(callback)){ callback(null,{
      			success:false
      		});}
      		Y.fire("UpdateUser",{success:false,user:null});
      	
      }
     }
     else
     {
     	if(Y.Lang.isFunction(callback)){ callback(null,{
      			success:false
      		});}
      	Y.fire("UpdateUser",{success:false,user:null});
     }
  	});
	
}
api.user ={
	is_taken:function(config){
		return {
			taken:false
		};
	},
	hash_password:function(config){
		var salt = "@PASSWORD_SALT@";
		return hash(config.val,salt);
	}
};

api.list = {
	"PostList":function(config,callback){
		 var tType= (config.query && config.query.tType) || "";
		 
		 if(tType==="fanpages"){
		 	db.view("posts/getFanpagePosts",{limit:10,descending:true,key:config.query.pageID},function(err,res){
				var list=[];
				  res.forEach(function (row) {
				  	row.id = row._id;
			        list.push(row);
			      });
			      callback(null,list);
			      return;
			});
		 }
		 else if(tType==="wardrobe-entry"){
		 	db.view("posts/getWardrobeEntries",{limit:10,descending:true,key:config.query.owner_id},function(err,res){
				var list=[];
				  res.forEach(function (row) {
				  	row.id = row._id;
			        list.push(row);
			      });
			      callback(null,list);
			      return;
			});
		 }
		 else{
			 db.view('posts/getPosts',{
			 	limit:10,
			 	descending:true
			 }, function (err, res) {
				  var list=[];
				  res.forEach(function (row) {
				  	row.id = row._id;
			        list.push(row);
			      });
			      callback(null,list);
			      return;
			  });
		}
		
	},
	"FanPageList":function(config,callback){
		db.view("pages/fanpages",{limit:10,descending:true},function(err,res){
			var list=[];
			  res.forEach(function (row) {
			  	row.id = row._id;
		        list.push(row);
		      });
		      callback(null,list);
		});
	}
};

api.db = {
	update:function(config,callback){
		var db,c;
		try{
		
		c = new(cradle.Connection)("http://@COUCHHOST@",5984,{
			cache:true,
			raw:false
		});
		db = c.database('@COUCHDB_NAME@');
		
		
		db.save('_design/user', {
		    views: {
		      byEmail: {
		        map: 'function(doc){ if(doc.email){ emit(doc.email,doc); }}'
		      }
		    }
		 },function(err,data){
		 	
		 	
		 });
		 db.save('_design/posts', {
		    views: {
		      getPosts: {
		        map: 'function(doc){ if(doc.type=="PostModel" && (!doc.tType || doc.tType=="publishing-page")){ emit(doc.created_at,doc); }}'
		      },
		      getFanpagePosts:{
		      	map:'function(doc){ if(doc.type==="PostModel" && doc.tType && doc.tType==="fanpages"){ emit(doc.owner_id,doc); } }'
		      },
		      getWardrobeEntries:{
		      	map:'function(doc){ if(doc.type==="PostModel" && doc.tType && doc.tType==="wardrobe-entry"){ emit(doc.author_id,doc); } }'
		      }
		    }
		 },function(err,data){
		 	
		 	
		 });
		 
		 db.save('_design/relations', {
		    views: {
		      getRelation: {
		        map: 'function(doc){ if(doc.type==="relation"){ emit([doc.source,doc.target],doc.relation); }}'
		      }
		    }
		 },function(err,data){
		 	
		 	
		 });
		 
		 
		 db.save('_design/pages', {
		    views: {
		      fanpages: {
		        map: 'function(doc){ if(doc.type=="FanPageModel"){ emit(doc.created_at,doc); }}'
		      }
		    }
		 },function(err,data){
		 	
		 	
		 });
		}catch(ex){
			Y.log("Exception in DB update"+ex);
		}
		
	}	
};

api.relations = {
	"getRelation":function(config,callback){
		db.view("/relations/getRelation",{limit:1,key:[config.source,config.target]},function(err,res){
			
			callback(null,[]);
		});
	}
};

exports.api = function(path,config,callback){
		var out,pathTokens;
		switch(path){
			
			case "/login":
				out = login(config,callback);
				return out;
			default:
				pathTokens = path.split("/");
				if(pathTokens.length>0)
				{
					api[pathTokens[1]][pathTokens[2]](config,callback); //0th one is empty string
				} 
				
				/*
			case "/user/is_taken":
				return api.user.is_taken(config,callback);
			case "/list/PostList":
				return api.list.PostList(config,callback);
			case "/list/FanPageList":
				return api.list.FanPageList(config,callback);
			case "/user/hash_password":
				return api.user.hash_password(config);
			case "/user/send_welcome_mail":
				return api.user.send_welcome_mail(config);
			case "/db/update":
				return api.db.update.apply(this,[config,callback]);
			case "/relations/getRelation":
				return api.relations.getRelation.apply(this,[config,callback]);
				*/
		}
	
}








